content.query:
--------------------------------------------------------------------------------
-- A.  CLEAN START: create schema if needed and drop old objects
--------------------------------------------------------------------------------
IF NOT EXISTS (SELECT * FROM sys.schemas WHERE name = 'BankCredit')
BEGIN
    EXEC('CREATE SCHEMA BankCredit');
END
GO

PRINT 'Schema BankCredit ensured';

-- drop final tables if exist
IF OBJECT_ID('BankCredit.dim_credit_history', 'U') IS NOT NULL DROP TABLE BankCredit.dim_credit_history;
IF OBJECT_ID('BankCredit.fact_loans', 'U') IS NOT NULL DROP TABLE BankCredit.fact_loans;
IF OBJECT_ID('BankCredit.dim_customers', 'U') IS NOT NULL DROP TABLE BankCredit.dim_customers;
GO

-- drop staging if present
IF OBJECT_ID('BankCredit.stg_customers','U') IS NOT NULL DROP TABLE BankCredit.stg_customers;
IF OBJECT_ID('BankCredit.stg_loans','U') IS NOT NULL DROP TABLE BankCredit.stg_loans;
IF OBJECT_ID('BankCredit.stg_credit_history','U') IS NOT NULL DROP TABLE BankCredit.stg_credit_history;
GO

-- drop external tables (if any)
IF OBJECT_ID('external_customers','U') IS NOT NULL DROP EXTERNAL TABLE external_customers;
IF OBJECT_ID('external_loans','U') IS NOT NULL DROP EXTERNAL TABLE external_loans;
IF OBJECT_ID('external_credit_history','U') IS NOT NULL DROP EXTERNAL TABLE external_credit_history;
GO

-- drop external data source and credential if exist (we will recreate)
IF EXISTS (SELECT * FROM sys.external_data_sources WHERE name = 'bank_raw_data_adls')
    DROP EXTERNAL DATA SOURCE bank_raw_data_adls;
GO

IF EXISTS (SELECT * FROM sys.database_scoped_credentials WHERE name = 'adls_sas_cred')
    DROP DATABASE SCOPED CREDENTIAL adls_sas_cred;
GO

PRINT 'Old objects removed (if existed)';
GO
